import React, { Component } from 'react';

class Chat extends Component {
  constructor(props) {
    super(props);

    this.state = {
      messages: [
        { isBot: true, content: 'hello from bot' },
        { isBot: false, content: 'hello from human' },
      ]
    }
  }

  render() {
    return (
      <div>
        <div className="mainChat">
          {messages.map((value, index) => {
          return <li key={index}>{value}</li>
        })}
          <div className="message botChat">hello from bot</div>
          <div className="message humanChat">hello from bot</div>
        </div>
        <div class="input">
          <input type="text" />
          <button>Send</button>
        </div>
      </div>
    )
  }
}

export default Chat;
