import React, { Component } from 'react';

class Chat extends Component {
  constructor(props) {
    super(props);

    this.state = {
      messages: [
        { id:1, isBot: true, content: 'hello from bot' },
        { id:2, isBot: false, content: 'hello from human' },
      ]
    }
  }

  render() {
    return (
      <div>
        <div className="mainChat">
          {this.state.messages.map((chat) => {
            return <div 
                  key={chat.id}
                  className={ chat.isBot ? 'botChat' : 'humanChat' }>
                  {chat.content}</div>
          })}

        </div>
        <div className="input">
          <input type="text" />
          <button>Send</button>
        </div>
      </div>
    )
  }
}

export default Chat;
